export class HeroService {
    // Example of service-in-service
    constructor(messageService, http) {
        this.messageService = messageService;
        this.http = http;
        this.heroesUrl = "api/heroes"; // URL to the web api
    }
    /* Log a HeroService message with the MessageService */
    log(message) {
        this.messageService.add(`HeroService: ${message}`);
    }
    handleError(operation = "operation", result) {
        return (error) => {
            // TODO: send the error to remote loggin infrastructure
            console.log(error); // log to console instead
            // TODO: better job of transforming error for user consumption
            this.log(`${operation} failed: ${error.message}`);
            return of(result);
        };
    }
    // Returns the mock heroes asynchronously with Http.get
    getHeroes() {
        return this.http.get(this.heroesUrl).pipe(tap((_) => this.log("featured heroes")), catchError(this.handleError("getHeroes", [])));
    }
    // getHero() for hero details and its id
    getHero(id) {
        // For now, assume that a hero with the specified `id` always exists.
        // Error handling will be added in the next step of the tutorial.
        const hero = HEROES.find((h) => h.id === id);
        this.messageService.add(`HeroService: fetched hero id=${id}`);
        return of(hero);
    }
}
